              ></el-dropdown-item
            >
          </el-dropdown-menu>
        </el-dropdown>
      </div>

      <div class="right-var-wrap" v-if="isLogin" style="display:inline-flex;">
        <div @click="toCart" style="width:80px;" class="cart">
          <i
            class="el-icon-shopping-cart-2"
            style="font-size:20px;"
          >
            <span>CART</span>
          </i>
        </div>
        <el-dropdown style="margin-left:18px;" class="dropdown">
          <span class="el-dropdown-link" style="font-size:20px;">
            <i class="el-icon-user"></i>
            <i class="el-icon-arrow-down el-icon--right"></i>
          </span>
          <el-dropdown-menu slot="dropdown">
            <el-dropdown-item
              ><el-link
                href="/setting"
                :underline="false"
                >SETTING</el-link
              ></el-dropdown-item
            >
            <el-dropdown-item divided
              ><el-link
                @click="logout"
                :underline="false"
                >LOG OUT</el-link
              ></el-dropdown-item
            >
          </el-dropdown-menu>
        </el-dropdown>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  name: "Header",
  components: {},
  props: {},

  data() {
    return {
      isLogin: false,
      userDTO: null,
      dialogFormVisible: false,
      host: "https://data.demostore.top",
      ruleForm: {
        username: "",
        password: "",
      },
      rules: {
        username: [
          {
            required: true,
            message: "Please enter an username",
            trigger: "blur",
          },
        ],
        password: [
          {
            required: true,
            message: "Please enter a password",
            trigger: "blur",
          },
          {
            min: 6,
            max: 16,
            message: "length between 3 to 16",
            trigger: "blur",
          },
        ],
      },
    };
  },
  computed: {},
  watch: {},
  mounted() {},
  created() {
    this.userDTO = JSON.parse(localStorage.getItem("user"));
    if (this.userDTO != null) {
      this.isLogin = true; // already logged in
    }
  },
  methods: {
    goToSetting() {
      this.$router.push("/setting"); // jump to user setting page
    },
    goToLogin() {
      this.dialogFormVisible = true; // login form
    },
    login() {
      return this.axios
        .post( "/user/login/normal", {
          username: this.ruleForm.username,
          password: this.ruleForm.password,
        })
        .then((result) => {
          this.userDTO = result.data.object;
          if (this.userDTO) {
            this.dialogFormVisible = false;
            this.isLogin = true;
            this.userDTO.authToken = "Bearer " + this.userDTO.authToken;
            localStorage.setItem("user", JSON.stringify(this.userDTO));
          } else {
            this.$message.error("Username and password unmatched");
          }
        })
        .catch((error) => {
          this.$message.error("login error!");
        });
    },

    toCart() {
      this.$router.push("/cart");
    },
    goToHomePage(){
      this.$router.push("/");
    },
    logout() {
      localStorage.setItem("user", null);
      this.isLogin = false;
      this.$message("Thanks for Using");
    },
    goToSetting() {

    }
  },
};
</script>

